/* USE : StoreManageMapper.getStoreExcelList */ SELECT '[' || tho.HQ_OFFICE_CD || '] ' || tho.HQ_OFFICE_NM AS HQ_OFFICE_CD_NM, tho.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, SUBSTR(tms.BIZ_NO,0,3)||'-'||SUBSTR(tms.BIZ_NO,4,2)||'-'||SUBSTR(tms.BIZ_NO,6,5) AS BIZ_NO, tms.AREA_CD, NVL(tms.CLS_FG, tho.CLS_FG) AS CLS_FG, NVL(tms.SYS_STAT_FG, tho.SYS_STAT_FG) AS SYS_STAT_FG, tho.SYS_STAT_FG AS HQ_SYS_STAT_FG, TO_CHAR(TO_DATE(tms.SYS_OPEN_DATE, 'YYYYMMDD') , 'YYYY/MM/DD') AS SYS_OPEN_DATE, tms.VAN_CD, tcvc.VAN_NM, tms.AGENCY_CD, tca.AGENCY_NM FROM TB_HQ_OFFICE tho, TB_MS_STORE tms, TB_CM_VAN_CMPNY tcvc, TB_CM_AGENCY tca WHERE tms.HQ_OFFICE_CD (+)= tho.HQ_OFFICE_CD AND tms.STORE_CD is not null AND tcvc.VAN_CD = tms.VAN_CD AND tcvc.VAN_FG = '01' AND tca.AGENCY_CD = tms.AGENCY_CD
AND tms.AGENCY_CD = #{agencyCd}
AND tho.HQ_OFFICE_CD LIKE '%'||#{hqOfficeCd}||'%'
AND tho.HQ_OFFICE_NM LIKE '%'||#{hqOfficeNm}||'%'
AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tms.BIZ_NO LIKE '%'||#{bizNo}||'%'
AND tms.CLS_FG = #{clsFg, jdbcType=VARCHAR}
AND tms.SYS_STAT_FG = #{sysStatFg, jdbcType=VARCHAR}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 선택한 매장브랜드가 있을 때
-- 선택한 매장브랜드가 없을 때('전체' 일때), 사용자 관리브랜드에 속한 정보만 조회 AND tms.HQ_BRAND_CD IN
#{item}
ORDER BY tho.HQ_OFFICE_CD, tms.STORE_CD, tms.SYS_OPEN_DATE ASC

