SELECT 'S'||LPAD(TO_CHAR(NVL(MAX(SUBSTR(STORE_CD, 2, 6)), 0) + 1), 6, '0') STORE_CD FROM TB_MS_STORE WHERE STORE_CD LIKE 'S%' AND LENGTH(STORE_CD) = 7

