/* USE : StoreManageMapper.getEnvGroupList */
SELECT tce2.ENVST_GRP_CD, tcn.NMCODE_NM AS ENVST_GRP_NM, tce2.ENVST_CD, tce2.ENVST_NM, tce2.DIRCT_IN_YN, tce2.REMARK, tce2.ENVST_VAL_CD, tce2.ENVST_VAL_NM, tce2.DEFLT_YN, tce2.TARGT_FG, DECODE(tms.ENVST_CD, NULL, 'N', 'Y') AS EXIST_FG, tms.ENVST_VAL AS SEL_ENVST_VAL, tce3.ENVST_CD_CNT, HDENV.HQ_ENVST_VAL_CD, HDENV.HQ_ENVST_VAL_NM FROM ( SELECT * FROM ( SELECT tce.ENVST_GRP_CD, tce.ENVST_CD, tce.ENVST_NM, tce.ENVST_FG, tce.TARGT_FG, tce.DIRCT_IN_YN, tce.ENVST_REMARK AS REMARK, tcd.ENVST_VAL_CD, tcd.ENVST_VAL_NM, tcd.DEFLT_YN, DECODE(tce.DIRCT_IN_YN, 'Y', 'Y', tcd.USE_YN) DTL_USE_YN FROM TB_CM_ENVST tce, TB_CM_ENVST_DTL tcd WHERE tce.TARGT_FG IN ('S','C','X') AND tce.ENVST_FG = #{envstFg} AND tce.USE_YN = 'Y' AND tcd.ENVST_CD (+)= tce.ENVST_CD
AND tce.ENVST_CD IN ( SELECT ENVST_CD FROM TB_CM_ENVST_HQ_MS_MNG WHERE ORGN_FG IN ('F', 'S') AND (ORGN_CD = #{storeCd} OR ORGN_CD = (SELECT HQ_OFFICE_CD FROM TB_MS_STORE WHERE STORE_CD = #{storeCd})) )
) tce2 WHERE tce2.DTL_USE_YN = 'Y'
AND (tce2.ENVST_CD LIKE '%'|| #{envst} ||'%' OR tce2.ENVST_NM LIKE '%'|| #{envst} ||'%')
) tce2, ( SELECT tce.ENVST_GRP_CD, COUNT(*) AS ENVST_CD_CNT FROM TB_CM_ENVST tce WHERE tce.TARGT_FG IN ('S','C','X') AND tce.ENVST_FG = #{envstFg} AND tce.USE_YN = 'Y' GROUP BY tce.ENVST_GRP_CD ) tce3, TB_MS_STORE_ENVST tms, TB_CM_NMCODE tcn, ( SELECT A.ENVST_CD , NVL(A.ENVST_VAL, 'NO_DATA_FOUND') HQ_ENVST_VAL_CD , DECODE(A.DIRCT_IN_YN, 'Y', NVL(A.ENVST_VAL, 'NO_DATA_FOUND'), NVL(B.ENVST_VAL_NM, 'NO_DATA_FOUND')) HQ_ENVST_VAL_NM FROM TB_HQ_ENVST A , TB_CM_ENVST_DTL B WHERE HQ_OFFICE_CD = (SELECT HQ_OFFICE_CD FROM TB_MS_STORE WHERE STORE_CD = #{storeCd}) AND B.ENVST_CD (+) = A.ENVST_CD AND B.ENVST_VAL_CD (+) = A.ENVST_VAL ) HDENV WHERE tms.STORE_CD (+)= #{storeCd} AND tms.ENVST_CD (+)= tce2.ENVST_CD AND tcn.NMCODE_GRP_CD = '004' AND tcn.NMCODE_CD = tce2.ENVST_GRP_CD AND tce3.ENVST_GRP_CD = tce2.ENVST_GRP_CD AND HDENV.ENVST_CD (+) = tce2.ENVST_CD ORDER BY tce2.ENVST_GRP_CD, tce2.ENVST_CD, tce2.ENVST_VAL_CD

