mapper.select:
/* USE : StoreManageMapper.getProdClass */ SELECT tmpc.P_PROD_CLASS_CD , tmpc.PROD_CLASS_CD , tmpc.PROD_CLASS_NM FROM TB_MS_PRODUCT_CLASS tmpc, ( SELECT tmp.STORE_CD, tmp.PROD_CLASS_CD, COUNT(tmp.PROD_CD) AS PROD_CD_CNT FROM TB_MS_PRODUCT_CLASS tmpc, TB_MS_PRODUCT tmp WHERE tmp.STORE_CD = #{storeCd} AND tmp.STORE_CD = tmpc.STORE_CD
AND tmp.PROD_CD IN (SELECT PROD_CD FROM TB_MS_PRINTER_PROD WHERE STORE_CD = #{storeCd} AND PRTER_NO = #{prterNo}) AND tmp.PROD_CLASS_CD = tmpc.PROD_CLASS_CD AND tmp.USE_YN = 'Y'
AND tmpc.PROD_CLASS_CD IN
#{item}
GROUP BY tmp.STORE_CD , tmp.PROD_CLASS_CD
AND tmp.PROD_CD NOT IN (SELECT PROD_CD FROM TB_MS_PRINTER_PROD WHERE STORE_CD = #{storeCd} AND PRTER_NO = #{prterNo}) AND tmp.PROD_CLASS_CD = tmpc.PROD_CLASS_CD AND tmp.USE_YN = 'Y'
AND tmpc.PROD_CLASS_CD IN
#{item}
GROUP BY tmp.STORE_CD , tmp.PROD_CLASS_CD
) tmp WHERE tmpc.store_cd = #{storeCd} AND tmp.STORE_CD = tmpc.STORE_CD START WITH tmpc.PROD_CLASS_CD = tmp.PROD_CLASS_CD AND tmpc.STORE_CD = #{storeCd} CONNECT BY tmpc.PROD_CLASS_CD = PRIOR tmpc.P_PROD_CLASS_CD AND tmpc.STORE_CD = #{storeCd} AND tmpc.P_PROD_CLASS_CD IS NOT NULL GROUP BY tmpc.P_PROD_CLASS_CD, tmpc.PROD_CLASS_CD, tmpc.PROD_CLASS_NM HAVING tmpc.PROD_CLASS_CD != '00000' ORDER BY P_PROD_CLASS_CD, PROD_CLASS_CD

