/* StoreManageMapper.unusualStoreRegInfo */
{
    CALL PKG_MS_STORE_REG_INFO.SP_STORE_REG_INFO_0001
    (
        #{storeCd},
        #{regId},
        #{result, mode=OUT, jdbcType=VARCHAR}
    )
}

/* StoreManageMapper.getHqNmcodeComboList */
SELECT
thn.NMCODE_CD AS VALUE,
thn.NMCODE_NM AS NAME
FROM TB_HQ_NMCODE thn
WHERE 1=1
AND thn.HQ_OFFICE_CD = #{hqOfficeCd}
AND thn.NMCODE_GRP_CD = #{nmcodeGrpCd}
ORDER BY thn.NMCODE_CD
