mapper.select:
/* USE : VirtualLoginMapper.getVirtualLoginList */
SELECT tms.HQ_OFFICE_CD, tms.HQ_OFFICE_NM, the.USER_ID AS HQ_USER_ID, tms.STORE_CD, tms.STORE_NM, tme.USER_ID AS MS_USER_ID, tms.CLS_FG, tcm_cls_fg.NMCODE_NM AS CLS_FG_NM, tms.SYS_STAT_FG, tcm_sys_stat_fg.NMCODE_NM AS SYS_STAT_FG_NM, tms.OWNER_NM, tms.TEL_NO, tme.MP_NO, tca.AGENCY_CD, tca.AGENCY_NM, tce.USER_ID AS CM_USER_ID, TO_CHAR(TO_DATE(tms.SYS_OPEN_DATE, 'YYYYMMDD') , 'YYYY/MM/DD') AS SYS_OPEN_DATE, TO_CHAR(TO_DATE(tms.SYS_CLOSURE_DATE, 'YYYYMMDD') , 'YYYY/MM/DD') AS SYS_CLOSURE_DATE, #{orgnFg} AS ORGN_FG
FROM ( SELECT A.HQ_OFFICE_CD , A.HQ_OFFICE_NM , B.HQ_BRAND_CD , B.BRANCH_CD , B.STORE_CD , B.STORE_NM , B.CLS_FG , B.SYS_STAT_FG , B.OWNER_NM , B.TEL_NO , B.AGENCY_CD , B.SYS_OPEN_DATE , B.SYS_CLOSURE_DATE FROM TB_HQ_OFFICE A , TB_MS_STORE B WHERE B.HQ_OFFICE_CD (+) = A.HQ_OFFICE_CD ) tms LEFT OUTER JOIN TB_MS_EMPLOYEE tme ON tms.STORE_CD = tme.STORE_CD AND tme.EMP_NO = '0000' LEFT OUTER JOIN TB_HQ_EMPLOYEE the ON tms.HQ_OFFICE_CD = the.HQ_OFFICE_CD AND the.EMP_NO = '0000' LEFT OUTER JOIN TB_CM_AGENCY tca ON tms.AGENCY_CD = tca.AGENCY_CD LEFT OUTER JOIN TB_CM_EMPLOYEE tce ON tca.AGENCY_CD = tce.AGENCY_CD AND tce.EMP_NO = '0000' LEFT OUTER JOIN TB_CM_NMCODE tcm_cls_fg ON tms.CLS_FG = tcm_cls_fg.NMCODE_CD AND tcm_cls_fg.NMCODE_GRP_CD = '001' LEFT OUTER JOIN TB_CM_NMCODE tcm_sys_stat_fg ON tms.SYS_STAT_FG = tcm_sys_stat_fg.NMCODE_CD AND tcm_sys_stat_fg.NMCODE_GRP_CD = '005' LEFT OUTER JOIN TB_MS_STORE_INFO tmsi ON tms.STORE_CD = tmsi.STORE_CD WHERE 1=1 -- 본사권한 접근시 AND tms.HQ_OFFICE_CD = #{hqOfficeCd}
-- 총판권한 접근시 AND tca.AGENCY_CD = #{agencyCd}
AND UPPER(tms.HQ_OFFICE_CD) LIKE '%' || UPPER(#{hqOfficeCd}) || '%'
AND tms.HQ_OFFICE_NM LIKE '%' ||#{hqOfficeNm} || '%'
AND UPPER(tms.STORE_CD) LIKE '%' || UPPER(#{storeCd}) || '%'
AND tms.STORE_NM LIKE '%' ||#{storeNm} || '%'
AND tms.CLS_FG = #{clsFg}
AND tms.SYS_STAT_FG = #{sysStatFg}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
ORDER BY tms.HQ_OFFICE_CD ASC, tms.STORE_CD ASC, tca.AGENCY_CD ASC
