mapper.select:
/* VirtualLoginAgencyMapper.getVirtualLoginAgencyrList */
SELECT CASE WHEN tca.P_AGENCY_CD = '00000' THEN 'P' ELSE 'C' END AS AGENCY_FG, tca.AGENCY_CD, tca.AGENCY_NM, tca.OWNER_NM, tce.USER_ID, tce.EMP_NM, tca.TEL_NO, tce.MP_NO, tcn.NMCODE_CD
FROM TB_CM_EMPLOYEE tce, TB_CM_AGENCY tca, ( SELECT NMCODE_CD FROM TB_CM_NMCODE WHERE 1=1 AND NMCODE_GRP_CD = '097' ) tcn WHERE 1=1 AND tce.AGENCY_CD = tca.AGENCY_CD AND tce.ADMIN_FG = tcn.NMCODE_CD AND tce.USE_YN = 'Y' AND tca.USE_YN = 'Y' AND tca.AGENCY_CD LIKE '%' || #{agencyCd} || '%' AND tca.AGENCY_NM LIKE '%' || #{agencyNm} || '%' AND tce.USER_ID LIKE '%' || #{userId} || '%' AND tcn.NMCODE_CD IN ('C','P') ORDER BY tca.AGENCY_CD, tce.USER_ID
