mapper.select:
/* USE : StoreInfoMapper.getStoreInfoList */ SELECT tms.STORE_CD , tms.STORE_NM , tms.OWNER_NM , tms.HQ_OFFICE_CD , (SELECT HQ_BRAND_NM FROM TB_HQ_BRAND thb WHERE thb.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thb.HQ_BRAND_CD = tms.HQ_BRAND_CD) AS HQ_BRAND_CD , tms.BIZ_NO , tms.BIZ_TYPE_CD , tms.BIZ_ITEM_CD , tms.BIZ_STORE_NM , tms.TEL_NO , tms.MP_NO , tms.FAX_NO , tms.EMAIL_ADDR , tms.HMPG_ADDR , tms.POST_NO , tms.ADDR , tms.ADDR_DTL , tms.AREA_CD , tms.STORE_TYPE , tms.CLS_FG , tms.SYS_STAT_FG , TO_CHAR(TO_DATE(tms.SYS_OPEN_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SYS_OPEN_DATE , (CASE WHEN tms.SYS_CLOSURE_DATE = '99991231' THEN null ELSE tms.SYS_CLOSURE_DATE END) AS SYS_CLOSURE_DATE , (SELECT tcvc.VAN_NM FROM TB_CM_VAN_CMPNY tcvc WHERE tcvc.VAN_FG ='01' AND tcvc.VAN_CD = tms.VAN_CD) AS VAN_CD , (SELECT tca.AGENCY_NM FROM TB_CM_AGENCY tca WHERE tca.AGENCY_CD = tms.AGENCY_CD) AS AGENCY_CD , tms.REMARK , tms.SYS_REMARK , tms.HD_REMARK , DECODE(tms.DIRECT_MANAGE_YN,'Y','직영','N','가맹') AS DIRECT_MANAGE_YN , tms.MAP_STORE_CD , tms.LATITUDE , tms.LONGITUDE , tms.BRANCH_CD , tms.SALE_AREA , tms.SITE_CD , tmsi.INDEX_NO , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP , tmsi.MOMS_AREA_FG , tmsi.MOMS_COMMERCIAL , tmsi.MOMS_SHOP_TYPE , tmsi.MOMS_STORE_MANAGE_TYPE FROM TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tms.STORE_CD = tmsi.STORE_CD (+)
AND tms.STORE_CD LIKE '%'||#{storeCd}||'%' --매장코드
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%' --매장명
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
ORDER BY tms.STORE_CD
