mapper.select:
/* USE : StorePosVersionMapper.getStorePosVersionList */ SELECT A.STORE_CD , C.STORE_NM , A.POS_NO , A.POS_NM , A.POS_VER_NO , A.VER_TYPE_FG , B.MAX_VER_SER_NO , E.MAX_SALE_DATE FROM TB_MS_POS A , ( SELECT PROG_FG , NVL(MAX(VER_SER_NO), '-') AS MAX_VER_SER_NO FROM TB_CM_POS_VERSN WHERE ORGN_CDS LIKE '%'|| #{hqOfficeCd} || '%' GROUP BY PROG_FG ) B , TB_MS_STORE C , TB_MS_STORE_INFO D , ( SELECT STORE_CD , POS_NO , TO_CHAR(TO_DATE(MAX(SALE_DATE), 'yyyymmdd'), 'yyyy-mm-dd') AS MAX_SALE_DATE FROM TB_SL_DAILY_POS WHERE SALE_DATE BETWEEN TO_CHAR(SYSDATE - 31, 'YYYYMMDD') AND TO_CHAR(SYSDATE, 'YYYYMMDD') AND HQ_OFFICE_CD = #{hqOfficeCd} GROUP BY STORE_CD , POS_NO ) E WHERE B.PROG_FG (+) = A.VER_TYPE_FG AND C.STORE_CD = A.STORE_CD AND D.STORE_CD (+) = A.STORE_CD AND E.STORE_CD (+) = A.STORE_CD AND E.POS_NO (+) = A.POS_NO AND C.HQ_OFFICE_CD = #{hqOfficeCd}
AND C.STORE_CD LIKE '%'||#{storeCd}||'%' --매장코드
AND C.STORE_NM LIKE '%'||#{storeNm}||'%' --매장명
AND C.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND C.HQ_BRAND_CD IN
#{item}
AND D.MOMS_TEAM = #{momsTeam}
AND D.MOMS_AC_SHOP = #{momsAcShop}
AND D.MOMS_AREA_FG = #{momsAreaFg}
AND D.MOMS_COMMERCIAL = #{momsCommercial}
AND D.MOMS_SHOP_TYPE = #{momsShopType}
AND D.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND C.BRANCH_CD = #{branchCd}
AND D.MOMS_STORE_FG_01 = #{momsStoreFg01}
ORDER BY A.STORE_CD, A.POS_NO
