mapper.select:
/* USE : AdminMediaMapper.getMediaList */
SELECT A.ADVER_FILE_NO AS VER_SER_NO , TO_CHAR(TO_DATE(A.ADVER_S_DATE, 'yyyymmdd'), 'yyyy-mm-dd') || ' ~ ' || TO_CHAR(TO_DATE(A.ADVER_E_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS USE_DATE , A.FILE_DESC AS VER_SER_NM , A.FILE_USE_TYPE , A.FILE_SIZE , A.USE_YN , A.FILE_ORG_NM || '.' || A.FILE_EXT AS FILE_ORG_NM , A.DISP_SEQ , A.DISP_TIME , A.FILE_NM AS FILE_NM_EXT , A.FILE_EXT , A.FILE_ORG_NM AS REAL_FILE_ORG_NM , A.LANG_FG , NVL(B.STORE_CNT, 0) AS STORE_CNT
FROM TB_CM_POS_ADVER_FILE A , ( SELECT ADVER_FILE_NO , COUNT(STORE_CD) AS STORE_CNT FROM TB_CM_POS_ADVER_STORE GROUP BY ADVER_FILE_NO ) B WHERE 1=1 AND A.ADVER_FILE_NO = B.ADVER_FILE_NO (+)
AND A.ADVER_FILE_NO LIKE '%' || #{verSerNo} || '%'
AND A.FILE_DESC LIKE '%' || #{verSerNm} || '%'
AND A.FILE_ORG_NM LIKE '%' || #{fileOrgNm} || '%'
AND A.USE_YN = #{useYn}
AND A.FILE_USE_TYPE = #{fileType}
AND #{startDate} BETWEEN A.ADVER_S_DATE AND A.ADVER_E_DATE
AND A.LANG_FG = #{langFg}
AND A.FILE_USE_TYPE != '008' ORDER BY A.ADVER_FILE_NO DESC

mapper.delete:
/* AdminMediaMapper.getMediaDelete */
DELETE  TB_CM_POS_ADVER_FILE
WHERE   1=1
AND     ADVER_FILE_NO = #{verSerNo}

/* AdminMediaMapper.getMediaHqStoreDelete */
DELETE  TB_CM_POS_ADVER_STORE
WHERE   1=1
AND     ADVER_FILE_NO   = #{verSerNo}

mapper.select:
/* USE : AdminMediaMapper.getFileType */
SELECT  TRIM(NMCODE_ITEM_2)
FROM    TB_CM_NMCODE
WHERE   NMCODE_GRP_CD ='303'
AND     NMCODE_CD = #{fileType}

/* USE : AdminMediaMapper.getFileTypeNm */
SELECT  NMCODE_NM
FROM    TB_CM_NMCODE
WHERE   NMCODE_GRP_CD ='303'
AND     NMCODE_CD = #{fileType}

/* USE : AdminMediaMapper.getDispSeq */
SELECT  NVL(MAX(DISP_SEQ), 0) +1 AS DISP_SEQ
FROM    TB_CM_POS_ADVER_FILE
WHERE   FILE_USE_TYPE = #{fileType}

mapper.insert:
/* USE : AdminMediaMapper.verRegist */
INSERT INTO TB_CM_POS_ADVER_FILE
(
    ADVER_FILE_NO
,   FILE_USE_TYPE
,   ADVER_S_DATE
,   ADVER_E_DATE
,   FILE_DIR
,   FILE_NM
,   FILE_EXT
,   FILE_SIZE
,   FILE_MIME_TYPE
,   FILE_ORG_NM
,   FILE_DESC
,   FILE_CHG_DT
,   USE_YN
,   REG_FG
,   REG_DT
,   REG_ID
,   MOD_DT
,   MOD_ID
,   DISP_SEQ
,   DISP_TIME
,   LANG_FG
,   ADVER_URL
)
VALUES
(
    #{verSerNo}
,   #{fileType}
,   #{startDate}
,   #{endDate}
,   #{fileDir}
,   #{fileNm}
,   #{fileExt}
,   #{fileSize}
,   #{fileMimeType}
,   #{fileOrgNm}
,   #{verSerNm}
,   #{regDt}
,   #{useYn}
,   'M'
,   #{regDt}
,   #{regId}
,   #{modDt}
,   #{modId}
,   #{dispSeq}
,   #{dispTime}
,   #{langFg}
,   #{adverUrl}
)

mapper.update:
/* USE : AdminMediaMapper.verModify */
UPDATE  TB_CM_POS_ADVER_FILE
SET     FILE_USE_TYPE   = #{fileType}
,       ADVER_S_DATE    = #{startDate}
,       ADVER_E_DATE    = #{endDate}

,       FILE_DIR        = #{fileDir}
,       FILE_NM         = #{fileNm}
,       FILE_EXT        = #{fileExt}
,       FILE_SIZE       = #{fileSize}
,       FILE_MIME_TYPE  = #{fileMimeType}
,       FILE_ORG_NM     = #{fileOrgNm}
,       FILE_CHG_DT     = #{modDt}

,       FILE_DESC       = #{verSerNm}
,       USE_YN          = #{useYn}
,       MOD_DT          = #{modDt}
,       MOD_ID          = #{modId}
,       DISP_TIME       = #{dispTime}
,       LANG_FG         = #{langFg}
,       ADVER_URL       = #{adverUrl}
WHERE   ADVER_FILE_NO   = #{verSerNo}

mapper.select:
/* USE : AdminMediaMapper.dtlInfo2 */
SELECT  FILE_NM
FROM    TB_CM_POS_ADVER_FILE
WHERE   ADVER_FILE_NO = #{verSerNo}

/* USE : AdminMediaMapper.chkDate */

    SELECT  COUNT(*)
    FROM    TB_CM_POS_ADVER_FILE
    WHERE   1=1
    AND     ADVER_S_DATE    <= #{endDate}
    AND     ADVER_E_DATE    >= #{startDate}
    AND     FILE_USE_TYPE   = #{fileType}
    AND     USE_YN          = #{useYn}


    AND ADVER_FILE_NO != #{verSerNo}

/* USE : AdminMediaMapper.chkFileTypeCnt */
SELECT  COUNT(*)
FROM    TB_CM_POS_ADVER_FILE
WHERE   FILE_USE_TYPE   = #{fileType}
AND     USE_YN          = 'Y'
AND     ADVER_FILE_NO   NOT IN (#{verSerNo})

mapper.update:
/* AdminMediaMapper.getMediaPlaySeqSaveUpdate */
UPDATE  TB_CM_POS_ADVER_FILE
SET
        DISP_SEQ    = #{dispSeq}
,       MOD_DT      = #{modDt}
,       MOD_ID      = #{modId}
WHERE   1=1
AND     ADVER_FILE_NO = #{verSerNo}

mapper.insert:
/* USE : AdminMediaMapper.getRegistStore */
INSERT INTO TB_CM_POS_ADVER_STORE
(
    HQ_OFFICE_CD
 ,  ADVER_FILE_NO
 ,  STORE_CD
 ,  REG_DT
 ,  REG_ID
 ,  MOD_DT
 ,  MOD_ID
 )
VALUES
(
    #{hqOfficeCd}
,   #{verSerNo}
,   #{storeCd}
,   #{regDt}
,   #{regId}
,   #{modDt}
,   #{modId}
)

mapper.delete:
/* USE : AdminMediaMapper.getRemoveStore */
DELETE  TB_CM_POS_ADVER_STORE
WHERE   ADVER_FILE_NO   = #{verSerNo}
AND     HQ_OFFICE_CD    = #{hqOfficeCd}
AND     STORE_CD        = #{storeCd}
