mapper.select:
/* AuthGroupMapper.selectGroup */ SELECT ROW_NUMBER() OVER (PARTITION BY 1 ORDER BY 1) NO, AUTH_GRP_CD, AUTH_GRP_NM, TARGET_ALL_FG, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '903' AND NMCODE_CD = A.TARGET_ALL_FG) AS TARGET_ALL_FG_NM, TARGET_ORGN, ( SELECT MAX(TARGET_ORGN_NM) FROM ( SELECT HQ_OFFICE_NM TARGET_ORGN_NM FROM TB_HQ_OFFICE WHERE HQ_OFFICE_CD = A.TARGET_ORGN UNION ALL SELECT AGENCY_NM TARGET_ORGN_NM FROM TB_CM_AGENCY WHERE AGENCY_CD = A.TARGET_ORGN ) ) AS TARGET_ORGN_NM, REMARK, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID FROM TB_WB_AUTHOR_GRP_INFO A
AND A.USE_YN = #{useYn}
AND A.GRP_NM LIKE '%'||#{authGrpNm}||'%'
AND A.AUTH_GRP_CD IN ( SELECT AUTH_GRP_CD FROM TB_WB_AUTHOR_GRP_INFO A WHERE TARGET_ALL_FG = 'A' UNION ALL SELECT AUTH_GRP_CD FROM TB_WB_AUTHOR_GRP_INFO A WHERE TARGET_ALL_FG = 'P' AND TARGET_ORGN = #{targetOrgn} )
ORDER BY AUTH_GRP_CD

/* AuthGroupMapper.selectGroupCnt */
SELECT COUNT(1)
  FROM TB_WB_AUTHOR_GRP_INFO A
 WHERE A.AUTH_GRP_CD = #{authGrpCd}

mapper.update:
/* AuthGroupMapper.updateGroup */
 UPDATE TB_WB_AUTHOR_GRP_INFO A
    SET A.AUTH_GRP_NM = #{authGrpNm},
        A.TARGET_ALL_FG = #{targetAllFg},
        A.TARGET_ORGN = #{targetOrgn},
        A.REMARK = #{remark},
        A.USE_YN = #{useYn},
        A.MOD_DT = #{modDt},
        A.MOD_ID = #{modId}
  WHERE A.AUTH_GRP_CD = #{authGrpCd}

mapper.delete:
/* AuthGroupMapper.deleteGroup */
 DELETE
   FROM TB_WB_AUTHOR_GRP_INFO A
  WHERE A.AUTH_GRP_CD = #{authGrpCd}

mapper.update:
/* AuthGroupMapper.mergeAuthorGrpResrce */ MERGE INTO TB_WB_AUTHOR_GRP_RESRCE A USING (SELECT #{authGrpCd} AS AUTH_GRP_CD, #{resrceCd} AS RESRCE_CD FROM DUAL ) ORG ON ( A.AUTH_GRP_CD = ORG.AUTH_GRP_CD AND A.RESRCE_CD = ORG.RESRCE_CD)
WHEN MATCHED THEN UPDATE SET A.USE_YN = #{useYn}, A.MOD_DT = #{regDt}, A.MOD_ID = #{regId}
WHEN NOT MATCHED THEN INSERT ( A.AUTH_GRP_CD, A.RESRCE_CD, A.USE_YN, A.REG_DT, A.REG_ID ) VALUES ( #{authGrpCd}, #{resrceCd}, #{useYn}, #{regDt}, #{regId} )

mapper.select:
/* AuthGroupMapper.selectAuthGrpCdById */
 SELECT AUTH_GRP_CD
   FROM TB_WB_USER_INFO A
  WHERE A.USER_ID = #{userId}

mapper.update:
/* AuthGroupMapper.mergeAuthorExcept */ MERGE INTO TB_WB_AUTHOR_EXCEPT A USING (SELECT #{userId} USER_ID, #{resrceCd} RESRCE_CD FROM DUAL ) ORG ON ( A.USER_ID = ORG.USER_ID AND A.RESRCE_CD = ORG.RESRCE_CD)
WHEN MATCHED THEN UPDATE SET A.USE_YN = #{useYn}, A.MOD_DT = #{modDt}, A.MOD_ID = #{modId}
WHEN NOT MATCHED THEN INSERT ( A.USER_ID, A.RESRCE_CD, A.INCLD_EXCLD_FG, A.USE_YN, A.REG_DT, A.REG_ID ) VALUES ( #{userId}, #{resrceCd}, #{incldExcldFg}, #{useYn}, #{regDt}, #{regId} )
