mapper.update:
/* USE : TemplateMapper.updateTemplateList */
UPDATE
    TB_CM_PRINT_TEMPL
SET
    TEMPLT_NM = #{templtNm},
    MOD_DT = #{modDt},
    MOD_ID = #{modId}
WHERE
    PRT_CLASS_CD = #{prtClassCd}
    AND TEMPLT_CD = #{templtCd}

mapper.delete:
/* USE : TemplateMapper.deleteTemplateList */
DELETE FROM 
    TB_CM_PRINT_TEMPL
WHERE
    PRT_CLASS_CD = #{prtClassCd}
    AND TEMPLT_CD = #{templtCd}
