mapper.select:
/* USE : EnvConfgMapper.getEnvstList */ SELECT tce.ENVST_CD, tce.ENVST_NM, tce.ENVST_FG, tcn_envst_fg.NMCODE_NM AS ENVST_FG_NM, tce.ENVST_GRP_CD, tcn_envst_grp_cd.NMCODE_NM AS ENVST_GRP_CD_NM, tce.DIRCT_IN_YN, tce.TARGT_FG, tcn_targt_fg.NMCODE_NM AS TARGT_FG_NM, tce.USE_YN, tce.REMARK FROM TB_CM_ENVST tce LEFT OUTER JOIN TB_CM_NMCODE tcn_envst_fg ON tce.ENVST_FG = tcn_envst_fg.NMCODE_CD AND tcn_envst_fg.NMCODE_GRP_CD = '003' LEFT OUTER JOIN TB_CM_NMCODE tcn_envst_grp_cd ON tce.ENVST_GRP_CD = tcn_envst_grp_cd.NMCODE_CD AND tcn_envst_grp_cd.NMCODE_GRP_CD = '004' LEFT OUTER JOIN TB_CM_NMCODE tcn_targt_fg ON tce.TARGT_FG = tcn_targt_fg.NMCODE_CD AND tcn_targt_fg.NMCODE_GRP_CD = '038'
tce.ENVST_CD = #{envstCd}
AND tce.ENVST_NM LIKE #{envstNm}||'%'
ORDER BY tce.ENVST_CD, tce.ENVST_FG, tce.ENVST_GRP_CD

mapper.update:
/* USE : EnvConfgMapper.updateEnvstDtl */
UPDATE
    TB_CM_ENVST_DTL
SET
    ENVST_VAL_NM = #{envstValNm,jdbcType=VARCHAR},
    DEFLT_YN = #{defltYn},
    USE_YN = #{useYn},
    MOD_DT = #{modDt},
    MOD_ID = #{modId}
WHERE
    ENVST_CD = #{envstCd}
    AND ENVST_VAL_CD = #{envstValCd}
