mapper.insert:
SELECT LPAD(SQ_WB_RESRCE.NEXTVAL, 6, '0') resrceCd FROM DUAL
/* WebMenuMapper.insertWebMenu */
INSERT INTO TB_WB_RESRCE_INFO ( RESRCE_CD, P_RESRCE, RESRCE_FG, FUNC_FG, RESRCE_NM, URL, SPCL_AUTHOR, DISP_LEVEL, DISP_IDX, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{resrceCd}, #{pResrce}, #{resrceFg}, #{funcFg}, #{resrceNm}, #{url}, #{spclAuthor}, #{dispLevel}, #{dispIdx}, #{useYn}, #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.update:
/* WebMenuMapper.deleteWebMenu */

UPDATE TB_WB_RESRCE_INFO A
   SET A.MOD_DT = #{modDt},
       A.MOD_ID = #{modId},
       A.FUNC_FG = #{funcFg},
       A.RESRCE_NM = #{resrceNm},
       A.URL = #{url},
       A.DISP_IDX = #{dispIdx},
       A.SPCL_AUTHOR = #{spclAuthor}
 WHERE A.RESRCE_CD = #{resrceCd}

/* WebMenuMapper.deleteWebMenu */

UPDATE TB_WB_RESRCE_INFO A
   SET A.USE_YN = 'N',
       A.MOD_DT = #{modDt},
       A.MOD_ID = #{modId}
 WHERE A.RESRCE_CD = #{resrceCd}

/* WebMenuMapper.deleteWebMenuAll */

UPDATE TB_WB_RESRCE_INFO A
   SET A.USE_YN = 'N',
       A.MOD_DT = #{modDt},
       A.MOD_ID = #{modId}
 WHERE A.RESRCE_CD IN (
       SELECT B.RESRCE_CD
         FROM TB_WB_RESRCE_INFO B
        WHERE B.USE_YN = 'Y'
        START WITH B.RESRCE_CD = #{resrceCd}
      CONNECT BY PRIOR B.RESRCE_CD = B.P_RESRCE
 )
