mapper.select:
/* WebLoginMapper.getWebLoginDayDetailList */ SELECT twlh.LOGIN_DATE, TO_CHAR(TO_DATE(twlh.LOGIN_DATE, 'YYYYMMDD'), 'DY') AS YOIL, (CASE WHEN twlh.STAT_CD = 'SUCC' THEN '성공' WHEN twlh.STAT_CD = 'FAIL' THEN '로그인 실패' WHEN twlh.STAT_CD = 'USE' THEN '사용여부' WHEN twlh.STAT_CD = 'NID' THEN '존재하지 않는 유져' WHEN twlh.STAT_CD = 'ERR' THEN '패스워드 틀림' WHEN twlh.STAT_CD = 'CHG' THEN '패스워드 변경 필요한 상태' WHEN twlh.STAT_CD = 'EXP' THEN '패스워드 유효 기간 지남' WHEN twlh.STAT_CD = 'CNT' THEN '로그인횟수 초과' ELSE null END ) AS STAT_NM, COUNT(twlh.SEQ) AS LOGIN_CNT FROM TB_WB_LOGIN_HIST twlh WHERE 1=1 AND twlh.LOGIN_DATE BETWEEN #{startDate} AND #{endDate}
AND twlh.USER_ID LIKE '%'||#{userId}||'%'
AND twlh.STAT_CD = 'SUCC'
AND twlh.STAT_CD != 'SUCC'
AND twlh.LOGIN_ORGN = #{loginOrgn}
GROUP BY twlh.LOGIN_DATE, twlh.STAT_CD ORDER BY twlh.LOGIN_DATE

/* WebLoginMapper.getWebLoginLoginDetailList */ SELECT twlh.LOGIN_DATE, twlh.LOGIN_IP, (CASE WHEN twlh.STAT_CD = 'SUCC' THEN '성공' WHEN twlh.STAT_CD = 'FAIL' THEN '로그인 실패' WHEN twlh.STAT_CD = 'USE' THEN '사용여부' WHEN twlh.STAT_CD = 'NID' THEN '존재하지 않는 유져' WHEN twlh.STAT_CD = 'ERR' THEN '패스워드 틀림' WHEN twlh.STAT_CD = 'CHG' THEN '패스워드 변경 필요한 상태' WHEN twlh.STAT_CD = 'EXP' THEN '패스워드 유효 기간 지남' WHEN twlh.STAT_CD = 'CNT' THEN '로그인횟수 초과' ELSE null END ) AS STAT_NM, twlh.LOGIN_ORGN, twlh.USER_ID, tce.USER_NM FROM TB_WB_LOGIN_HIST twlh, ( ( --본사 SELECT A.USER_ID, 'H' AS ORGN_FG, A.HQ_OFFICE_CD AS ORGN_CD, B.HQ_OFFICE_NM AS ORGN_NM, A.EMP_NM AS USER_NM, A.EMP_NO, '' AS STORE_CD, '' AS STORE_NM, A.HQ_OFFICE_CD, B.HQ_OFFICE_NM, A.HQ_OFFICE_CD AS ORGN_GRP_CD, B.HQ_OFFICE_NM AS ORGN_GRP_NM, '' AS P_AGENCY_CD FROM TB_HQ_EMPLOYEE A, TB_HQ_OFFICE B WHERE 1=1 AND A.HQ_OFFICE_CD = B.HQ_OFFICE_CD ) UNION ALL ( --매장 SELECT A.USER_ID, 'S' AS ORGN_FG, A.STORE_CD AS ORGN_CD, B.STORE_NM AS ORGN_NM, A.EMP_NM AS USER_NM, A.EMP_NO, A.STORE_CD AS STORE_CD, B.STORE_NM AS STORE_NM, C.HQ_OFFICE_CD, C.HQ_OFFICE_NM, DECODE( C.HQ_OFFICE_CD, '00000', A.STORE_CD, C.HQ_OFFICE_CD ) AS ORGN_GRP_CD, DECODE( C.HQ_OFFICE_CD, '00000', B.STORE_NM, C.HQ_OFFICE_NM ) AS ORGN_GRP_NM, '' AS P_AGENCY_CD FROM TB_MS_EMPLOYEE A, TB_MS_STORE B, TB_HQ_OFFICE C WHERE 1=1 AND A.STORE_CD = B.STORE_CD AND B.HQ_OFFICE_CD = C.HQ_OFFICE_CD ) UNION ALL ( --관리자,총판 SELECT A.USER_ID, DECODE( A.ADMIN_FG, 'A', 'M', 'A' ) AS ORGN_FG, A.AGENCY_CD AS ORGN_CD, B.AGENCY_NM AS ORGN_NM, A.EMP_NM AS USER_NM, A.EMP_NO, '' AS STORE_CD, '' AS STORE_NM, '' AS HQ_OFFICE_CD, '' AS HQ_OFFICE_NM, A.AGENCY_CD AS ORGN_GRP_CD, B.AGENCY_NM AS ORGN_GRP_NM, B.P_AGENCY_CD AS P_AGENCY_CD FROM TB_CM_EMPLOYEE A, TB_CM_AGENCY B WHERE 1=1 AND A.AGENCY_CD = B.AGENCY_CD ) ) tce WHERE 1=1 AND tce.USER_ID (+)= twlh.USER_ID AND twlh.LOGIN_DATE BETWEEN #{startDate} AND #{endDate}
AND twlh.USER_ID LIKE '%'||#{userId}||'%'
AND twlh.STAT_CD = 'SUCC'
AND twlh.STAT_CD != 'SUCC'
AND twlh.LOGIN_ORGN = #{loginOrgn}
ORDER BY twlh.LOGIN_DATE, twlh.LOGIN_DT
